class NotificationService {
  constructor() {
    this.permission = 'default';
    this.soundEnabled = true;
    this.desktopEnabled = true;
    this.originalTitle = document.title;
    this.unreadCount = 0;
    this.notificationSound = null;
    this.initializeSound();
    this.checkPermission();
  }

  initializeSound() {
    // Create notification sound
    this.notificationSound = new Audio('data:audio/wav;base64,');
    this.notificationSound.volume = 0.3;
  }

  async checkPermission() {
    if (!('Notification' in window)) {
      console.log('This browser does not support notifications');
      return;
    }

    if (Notification.permission === 'granted') {
      this.permission = 'granted';
    } else if (Notification.permission !== 'denied') {
      const permission = await Notification.requestPermission();
      this.permission = permission;
    }
  }

  async requestPermission() {
    if (!('Notification' in window)) {
      return false;
    }

    const permission = await Notification.requestPermission();
    this.permission = permission;
    return permission === 'granted';
  }

  playSound() {
    if (!this.soundEnabled) return;
    
    try {
      this.notificationSound.currentTime = 0;
      this.notificationSound.play().catch(e => {
        console.log('Could not play notification sound:', e);
      });
    } catch (error) {
      console.error('Error playing sound:', error);
    }
  }

  showDesktopNotification(title, options = {}) {
    if (!this.desktopEnabled || this.permission !== 'granted') return;
    
    // Don't show notification if window is focused
    if (document.hasFocus()) return;

    try {
      const notification = new Notification(title, {
        icon: '/logo192.png',
        badge: '/logo192.png',
        tag: 'focal-point-chat',
        renotify: true,
        ...options
      });

      notification.onclick = () => {
        window.focus();
        notification.close();
        
        // Navigate to conversation if provided
        if (options.data?.conversationId) {
          window.dispatchEvent(new CustomEvent('navigate-to-conversation', {
            detail: { conversationId: options.data.conversationId }
          }));
        }
      };

      // Auto close after 5 seconds
      setTimeout(() => notification.close(), 5000);

      return notification;
    } catch (error) {
      console.error('Error showing notification:', error);
    }
  }

  notifyNewMessage(message, conversationName, senderName) {
    // Play sound
    this.playSound();

    // Show desktop notification
    this.showDesktopNotification(`New message from ${senderName}`, {
      body: message.text || 'Sent a file',
      data: { conversationId: message.conversationId }
    });
  }

  updateTabTitle(unreadCount) {
    this.unreadCount = unreadCount;
    
    if (unreadCount > 0) {
      document.title = `(${unreadCount}) ${this.originalTitle}`;
      
      // Add favicon badge (optional - requires favicon library)
      this.updateFavicon(unreadCount);
    } else {
      document.title = this.originalTitle;
      this.resetFavicon();
    }
  }

  updateFavicon(count) {
    // Create canvas for favicon with badge
    const canvas = document.createElement('canvas');
    canvas.width = 32;
    canvas.height = 32;
    const ctx = canvas.getContext('2d');
    
    // Draw original favicon
    const favicon = new Image();
    favicon.onload = () => {
      ctx.drawImage(favicon, 0, 0, 32, 32);
      
      // Draw badge
      if (count > 0) {
        ctx.fillStyle = '#ef4444';
        ctx.beginPath();
        ctx.arc(24, 8, 8, 0, 2 * Math.PI);
        ctx.fill();
        
        ctx.fillStyle = 'white';
        ctx.font = 'bold 10px Arial';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText(count > 99 ? '99+' : count.toString(), 24, 8);
      }
      
      // Update favicon
      const link = document.querySelector("link[rel*='icon']") || document.createElement('link');
      link.type = 'image/x-icon';
      link.rel = 'shortcut icon';
      link.href = canvas.toDataURL();
      document.getElementsByTagName('head')[0].appendChild(link);
    };
    favicon.src = '/favicon.ico';
  }

  resetFavicon() {
    const link = document.querySelector("link[rel*='icon']");
    if (link) {
      link.href = '/favicon.ico';
    }
  }

  setSoundEnabled(enabled) {
    this.soundEnabled = enabled;
    localStorage.setItem('chat_sound_enabled', enabled);
  }

  setDesktopEnabled(enabled) {
    this.desktopEnabled = enabled;
    localStorage.setItem('chat_desktop_notifications', enabled);
    
    if (enabled) {
      this.requestPermission();
    }
  }

  loadSettings() {
    const soundSetting = localStorage.getItem('chat_sound_enabled');
    const desktopSetting = localStorage.getItem('chat_desktop_notifications');
    
    this.soundEnabled = soundSetting !== 'false';
    this.desktopEnabled = desktopSetting !== 'false';
  }

  // Check if user has interacted with page (for autoplay policy)
  enableSoundAfterInteraction() {
    const enableSound = () => {
      this.notificationSound.play().then(() => {
        this.notificationSound.pause();
        this.notificationSound.currentTime = 0;
      }).catch(() => {});
      
      document.removeEventListener('click', enableSound);
      document.removeEventListener('keydown', enableSound);
    };
    
    document.addEventListener('click', enableSound);
    document.addEventListener('keydown', enableSound);
  }
}

export default new NotificationService();